import React, { Component } from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom'
import Spinner from '../layouts/Spinner'

class Lyrics extends Component {
    state = {
        track: {},
        lyrics: {}
    }

   async componentDidMount() {
        const res = await axios
        .get(
          `https://cors-anywhere.herokuapp.com/http://api.musixmatch.com/ws/1.1/track.lyrics.get?
          track_id=${
            this.props.match.params.id
          }&apikey=${process.env.REACT_APP_MM_KEY}`
        )

        this.setState({lyrics: res.data.message.body.lyrics})

        const res2 = await axios.get(
            `https://cors-anywhere.herokuapp.com/http://api.musixmatch.com/ws/1.1/track.get?
            track_id=${
              this.props.match.params.id
            }&apikey=${process.env.REACT_APP_MM_KEY}`
          );
    
        this.setState({track: res2.data.message.body.track})
    }
    render() {
        const {track , lyrics} = this.state;
        if(track === undefined || lyrics === undefined ||
         Object.keys(track).length === 0 ||
         Object.keys(lyrics).length === 0)
         {
            return <Spinner />
         } else {
            return (
            <React.Fragment>
                <Link to = "/" className = "btn btn-dark btn-sm mb-4">Go Back</Link>
                <div className = "card">
                    <h5 className = "card-header">
                        {track.track_name} by {' '}
                        <span className = "text-secondary">{track.artist_name}</span>
                    </h5>
                    <div className = "card-body">
                        <p className = "text-card">{lyrics.lyrics_body}</p>
                    </div>
                    <ul className = "list-group mt-3">
                        <li className = "list-group-item">
                            <strong>Album ID</strong>: {track.album_id}
                        </li>
                        <li className = "list-group-item">
                            <strong>Song Genre</strong>: {track.primary_genres.music_genre_list[0]
                            .music_genre.music_genre_name}
                        </li>
                    </ul>
                </div>
            </React.Fragment>
            )
         }
    }
}

export default Lyrics;
