import React, { Component , createContext } from 'react';
import axios from 'axios'

const Context = createContext()

const reducer = (state , action) => {
    switch(action.type) {
        case 'SEARCH_TRACKS':
            return {
                ...state,
                track_list:action.payload,
                headding: 'Search Reasults'
            };
        default :
        return state;
    }
}
export class Provider extends Component {
    state = {
        track_list: [],  
        headding: 'Top 10 Tracks',
        dispatch: action => {this.setState(state => reducer(action , state))}
    }

    componentDidMount() {
        axios.get(`https://cors-anywhere.herokuapp.com/https://api.musixmatch.com/ws/1.1/chart.tracks.get?
        chart_name=top&page=1&page_size=10&country=eg&f_has_lyrics=1&
        apikey=${process.env.REACT_APP_MM_KEY}`)

        .then(res => {
            //console.log(res.data)
            this.setState({track_list: res.data.message.body.track_list})
        })
        .catch(err => console.log(err))
    }

    render() {
        return (
            <Context.Provider value = {this.state}>
                {this.props.children}
            </Context.Provider>
        )
    }
}

export const Consumer = Context.Consumer;
